import React from 'react'
import Navbar from './../Components/Navbar';
import { Announcement } from './../Components/Announcement';
import styled from 'styled-components';

const Container = styled.div`
    width: 100%;
`
const Wrapper = styled.div`
    width: 90%;
    height: 80vh;
    margin: auto;
    margin-top: 30px;
    display: flex;
`
const Button = styled.div``
const ImageContainer = styled.div`
    flex: 1;
    `
const Image = styled.img`
    height: 100%;
    `
const DescContainer = styled.div`
    flex: 1;
    `
const Title = styled.h1`
    font-weight: 100;
    margin: 0 0 30px 0;
    `
const Desc = styled.p`
    margin-bottom: 40px;
    `
const Price = styled.h1`
    font-weight: 100;
    margin: 0 0 30px 0;
    `
const Specification = styled.div`
    display: flex;
    width: 60%;
    border: 3px solid teal;
    justify-content: space-between;
    align-items: center;
`
const ColorContainer = styled.div`
    display: flex;
    width: 50%;
    justify-content: space-between;
    border: 2px solid red;
    align-items: center;
`
const SizeContainer = styled.div`
    display: flex;
    width: 35%;
    border: 1px solid blue;
    margin-left: 20px;


`
const Text = styled.div`
    width: 40%;
    font-size: 20px;
`
const Colors = styled.div`
    display: flex;
    border: 2px solid black;
    width: 65%;
    justify-content: space-between;
`
const Color = styled.div`
    width: 25px;
    height: 25px;
    background-color: red;
    border-radius: 50%;
`
const Select = styled.select`
    flex: 1;
`
const Option = styled.option``
const ButtonContainer = styled.div``
const NoOfItems = styled.div``
const SignedButton = styled.div``
const CartButtonContainer = styled.div``

const Item = () => {
    return (
        <Container>
            <Navbar />
            <Announcement />
            <Wrapper>
                <ImageContainer>
                    <Image src="https://i.ibb.co/S6qMxwr/jean.jpg"></Image>
                </ImageContainer>
                <DescContainer>
                    <Title>Denim Jumpsuit</Title>
                    <Desc>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Sunt doloribus illo autem magni eum magnam in, quia, repudiandae iusto id distinctio. Sed, id est? Porro ut officia saepe molestiae, aliquid vitae ratione blanditiis harum sit reprehenderit repellendus hic error autem dolores beatae inventore quo perferendis iste delectus asperiores? Eaque, eligendi.</Desc>
                    <Price>$20</Price>
                    <Specification>
                        <ColorContainer>
                            <Text>Color</Text>
                            <Colors>
                            <Color></Color>
                            <Color></Color>
                            <Color></Color>
                            </Colors>
                        </ColorContainer>
                        <SizeContainer>
                            <Text>Size</Text>
                            <Select>
                                <Option>M</Option>
                                <Option>L</Option>
                                <Option>XL</Option>
                                <Option>XXL</Option>
                                <Option>XXXL</Option>
                            </Select>
                        </SizeContainer>
                    </Specification>
                    <ButtonContainer>
                        <NoOfItems>
                        <SignedButton>_</SignedButton>
                        <Text>1</Text>
                        <SignedButton>+</SignedButton>
                        </NoOfItems>
                        <CartButtonContainer>
                            <Button>ADD TO CART</Button>
                        </CartButtonContainer>
                    </ButtonContainer>
                </DescContainer>
            </Wrapper>
        </Container>
    )
}

export default Item
