import React from 'react'
import Navbar from './../Components/Navbar';
import Footer from './../Components/Footer';
import NewsLetter from './../Components/NewsLetter';
import Announcement from './../Components/Announcement';
import styled from 'styled-components';

const Container = styled.div`
  width: 100%;   
`

const MainContainer = styled.div`
  width: 95%;
  border: 1px solid lightgray;
  margin: auto;
`

const Wrapper = styled.div`
  
`

const Title = styled.h1`
  text-align: center;
  font-weight: 100;
`
const ButtonsBar = styled.div`
  display: flex;
  justify-content: space-between;
`
const MiddleButtonBar = styled.div`
  display: flex;
  flex: 1;
  justify-content: space-evenly;
  align-items: center;
`

const ButtonContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: ${props => props.position === "Right" ? "flex-end" : "flex-start"};
`

const A = styled.a`
  color: black;
  font-weight: bold;
`
const Button = styled.button`
  font-weight: bold;
  background-color: ${props => props.buttonType === "checkout" ? "black" : "transparent"};
  color: ${props => props.buttonType === "checkout" ? "white" : "black"};
  width: 50%;
  height: 40px;
`

const Cart = () => {
  return (
        <Container>
            <Navbar/>
            <Announcement/>
            <MainContainer>
              <Title>YOUR BAG</Title>
              <ButtonsBar>
                <ButtonContainer position={"Left"}>
                <Button buttonType={"shopping"}>CONTINUE SHOPPING</Button>
                </ButtonContainer>
                <MiddleButtonBar>
                  <A href="#">Shopping Bag(2)</A>
                  <A href="#">Your Wishlist(0)</A>
                </MiddleButtonBar>
                <ButtonContainer position={"Right"}>
                <Button buttonType={"checkout"}>CHECKOUT NOW</Button>
                </ButtonContainer>
              </ButtonsBar>
              <Wrapper>

              </Wrapper>
            </MainContainer>
            <NewsLetter/>
            <Footer/>
        </Container>
  )
}

export default Cart
