import React from "react";
import styled from "styled-components";
import {ArrowBackIosNewOutlined, ArrowForwardIosOutlined} from "@mui/icons-material";

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
`;

const Arrow = styled.div`
    height: 40px;
    width: 40px;
    background-color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto;
    ${props => props.direction === "left" ? {left:"20px"} : {right:"20px"}};
    cursor: pointer;
`
const Wrapper = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
`
const Slide = styled.div`
    width:100vw;
    height:100vh;
    display: flex;
    aling-items: center;
    background-color: #ff76000f;
`

const ImgContainer = styled.div`
    flex: 1;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
`
const Image = styled.img`
    height: 100%;
`

const InfoContainer = styled.div`
    flex: 1;
    align-items: center;
    justify-content: center;
    display: flex;
    flex-direction: column;

`

const Title = styled.h1`
    font-size: 100px;
`

const Info = styled.p`
    letter-spacing: 5px;
    font-size: 30px;
`
const Button = styled.button`
    background-color: transparent;
    cursor: pointer;
    width: 200px;
    height: 50px;
    font-size: 20px;
    font-weight: bolder;
    margin-top: 40px;
`
export const Slider = () => {
    return(
        <Container>
            <Arrow direction="left">
                <ArrowBackIosNewOutlined />
            </Arrow>
            <Wrapper>
                <Slide>
                    <ImgContainer>
                        <Image src="https://i.ibb.co/cNVT1b4/model.png" alt="Not Loading"/>
                    </ImgContainer>
                    <InfoContainer>
                        <Title>
                            SUMMER SALE
                        </Title>
                        <Info>
                            DON'T COMPROMISE ON STYLE? GET FLAT 30% OFF FOR NEW ARRIVALS.
                        </Info>
                        <Button>
                           SHOP NOW
                        </Button>
                    </InfoContainer>
                </Slide>
                <Slide>
                    <ImgContainer>
                        <Image src="https://i.ibb.co/cNVT1b4/model.png" alt="Not Loading"/>
                    </ImgContainer>
                    <InfoContainer>
                        <Title>
                            SUMMER SALE
                        </Title>
                        <Info>
                            DON'T COMPROMISE ON STYLE? GET FLAT 30% OFF FOR NEW ARRIVALS.
                        </Info>
                        <Button>
                           SHOP NOW
                        </Button>
                    </InfoContainer>
                </Slide>
                <Slide>
                    <ImgContainer>
                        <Image src="https://i.ibb.co/cNVT1b4/model.png" alt="Not Loading"/>
                    </ImgContainer>
                    <InfoContainer>
                        <Title>
                            SUMMER SALE
                        </Title>
                        <Info>
                            DON'T COMPROMISE ON STYLE? GET FLAT 30% OFF FOR NEW ARRIVALS.
                        </Info>
                        <Button>
                           SHOP NOW
                        </Button>
                    </InfoContainer>
                </Slide>
            </Wrapper>
            <Arrow direction = "right">
                <ArrowForwardIosOutlined />
            </Arrow>
        </Container>
    )
};

export default Slider;
