import React from "react";
import styled from "styled-components";
import {ArrowBackIosNewOutlined, ArrowForwardIosOutlined} from "@mui/icons-material";

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
`;

const Arrow = styled.div`
    height: 40px;
    width: 40px;
    background-color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto;
    ${props => props.direction === "left" ? {left:"20px"} : {right:"20px"}};
    opacity: 0.5;
    cursor: pointer;
`
const Wrapper = styled.div`
    background-color: rgb(240, 255, 255);
    width: 100%;
    height: 100%;
`
const Slides = styled.div`
    display: flex;
`

const ImgContainer = styled.div`
    flex: 1;
    display: flex;
    justify-content: center;
`
const Image = styled.img`
    height: 100vh;
`

const InfoContainer = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

`

const Title = styled.p`
    font-size: 100px;
    font-weight: bolder;
`

const Info = styled.p`
    letter-spacing: 3px;
    font-size: 30px;
`
const Button = styled.button`
    background-color: transparent;
    cursor: pointer;
    width: 200px;
    height: 50px;
    font-size: 50px
`
export const Slider = () => {
    return(
        <Container>
            <Arrow direction="left">
                <ArrowBackIosNewOutlined />
            </Arrow>
            <Wrapper>
                <Slides>
                    <ImgContainer>
                        <Image src="https://i.ibb.co/cNVT1b4/model.png" alt="Not Loading"/>
                    </ImgContainer>
                    <InfoContainer>
                        <Title>
                            Summer Sale
                        </Title>
                        <Info>
                            Right now we are have discount up to 50% on every items.
                        </Info>
                        <Button>
                            Buy Now
                        </Button>
                    </InfoContainer>
                </Slides>
            </Wrapper>
            <Arrow direction = "right">
                <ArrowForwardIosOutlined />
            </Arrow>
        </Container>
    )
};

export default Slider;
