import React from 'react'
import styled from 'styled-components'
import {Facebook, Instagram, Twitter, Pinterest} from '@mui/icons-material'

const Container = styled.div`
height: 35vh;
width: 100%;
display: flex;
justify-content: space-between;
margin: 10px;
`
const Left = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    margin-left: 10px;

`

const Title = styled.h1`
    font-size: 40px;
`
const Info = styled.div`
    font-weight: bold;
`
const IconContainer = styled.div`
    display:flex;
    width: 100%;
    margin-top: 15px;
`
const Icon = styled.div`
    width: 40px;
    height: 40px;
    margin: 5px;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: ${props => props.color};
    color: white;
    border-radius: 50%;
    margin-right: 20px;
    cursor: pointer;
`

const Center = styled.div`
background-color: tomato;
flex: 1
`
const Right = styled.div`
background-color: lightpink;
flex: 1
`


const Footer = () => {
  return (
    <Container>
        <Left>
            <Title>LAMA.</Title>
            <Info>There are many variations of passages of Lorem Ipsum available, but the majority have suffered alterationin some form. by injected humour, or randamised words which don't look even slightly believable.</Info>
            <IconContainer>
                <Icon color="#4867AA"><Facebook style={{fontSize: "35px"}}/></Icon>
                <Icon color="#F7006F"><Instagram style={{fontSize: "35px"}}/></Icon>
                <Icon color="#1DA1F2"><Twitter style={{fontSize: "35px"}}/></Icon>
                <Icon color="#FD002C"><Pinterest style={{fontSize: "35px"}}/></Icon>
            </IconContainer>
        </Left>
        <Center>Center</Center>
        <Right>Right</Right>
    </Container>
  )
}

export default Footer
